import React from 'react'
import "antd/dist/antd.css"
import {
    Card,
    Divider,
    Avatar,
    Typography,
    Button
} from 'antd'
import {connect} from 'react-redux'
import axios from 'axios'
import moment from 'moment';
import user from '../models/user'
import select from '../models/select';



const mapPropsToState = state => {
    return {
        user: state.user,
        select: state.select
    }
  }
class Randomusr extends React.Component {
    componentDidMount(){
      this.getUserInfor()
    }
    getUserInfor(){
      axios.get('https://randomuser.me/api')
            .then(res => {
              const {dispatch}=this.props
              dispatch(
                {
                  type: 'SET_USER',
                  user: res.data.results[0]
                }
              )
            })
            .catch(e => {
              console.log(e.toString())
            })
    }
    handleChange = name => event => {
      const {dispatch}=this.props
      dispatch(
        {
          type: 'SELECT',
          select: name
        }
      )
    }
      render(){
        const { user, select } = this.props
        return (
          <div style={{ background: "#ECECEC", padding: "25px 250px" }}>
          <Card  bordered={false} style={{ width: 500 }}>
                <Divider>
                <Avatar   
                    size={120}
                    src={user.picture ? user.picture.large : ''} 
                />
                </Divider>
                  {
                    select === 'home' ?
                    <div>
                      <Typography color="secondary">My name is </Typography>
                      <h1>${user.name.title}. ${user.name.first} ${user.name.last} </h1>
                    </div>
                    : ''
                  }
                  {
                    select === 'email' ?
                    <div>
                      <Typography color="secondary">My email is </Typography>
                      <h1>{user.email} </h1>
                    </div>
                    : ''
                  }
                  {
                    select === 'birthday' ?
                    <div>
                      <Typography color="secondary">Helu, My DOB is </Typography>
                      <h1>{user.dob ? moment(user.dob.date).format('DD/MM/YYYY') : ''}</h1>
                    </div>
                    : ''
                  }
                  {
                    select === 'address' ?
                    <div>
                      <Typography color="secondary">Helu, My address is </Typography>
                      {/* <h1>{user.location ? ${user.location.street.number} ${user.location.street.name} : ''} </h1> */}
                    </div>
                    : ''
                  }
                  {
                    select === 'phone' ?
                    <div>
                      <Typography color="secondary">Helu, My phone-number is </Typography>
                      <h1>{user.phone ? user.phone : ''} </h1>
                    </div>
                    : ''
                  }
                  {
                    select === 'password' ?
                    <div>
                      <Typography color="secondary">Helu, My pw is </Typography>
                      <h1>{user.nat ? user.nat : ''} </h1>
                    </div>
                    : ''
                  }
                  <Button color="primary" size='large' icon="user" onClick={this.handleChange('home')} />
                  <Button color="primary" size='large' icon="mail" onClick={this.handleChange('email')} />
                  <Button color="primary" size='large' icon="phone" onClick={this.handleChange('phone')} />
                  <Button color="primary" size='large' icon="calendar" onClick={this.handleChange('birthday')} />
                  <Button color="primary" size='large' icon="home" onClick={this.handleChange('address')} />
                  <Button color="primary" size='large' icon="phone" onClick={this.handleChange('phone')} />
                  <Button color="primary" size='large' icon="lock" onClick={this.handleChange('password')} />
          </Card>
          </div>
        )
  
      }
  }

  export default connect(mapPropsToState)(Randomusr);
